import axios from "axios";
import { observable, runInAction, toJS } from "mobx";
import jwt_decode from "jwt-decode";

let authMethods = observable({
  login: (loginData, history) => {
    console.log(loginData);
    axios
      .post("http://localhost:8080/api/signin", {
        withCredentials: true,
        username: loginData.username,
        password: loginData.password,
      })
      .then(function (response) {
        console.log("로그인 성공");
        console.log(response);
        authMethods.setDefaultAxiosRequestHeader(response);
        authMethods.setUserInfo(response);
        runInAction(() => {
          authorizationInfo.isAuthorized = true;
        });
        console.log(authorizationInfo.isAuthorized);
        history.replace("/");
      })
      .catch(function (error) {
        console.log("로그인 실패");
        console.log(error);
      });
  },
  logout: () => {
    axios.defaults.headers.common["Authorization"] = null;
    runInAction(() => {
      authorizationInfo.isAuthorized = false;
    });
  },
  setDefaultAxiosRequestHeader: (response) => {
    const { token } = response.data;
    // console.log(response.data);
    // accessToken 설정
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  },
  setUserInfo: (response) => {
    const { token } = response.data;
    userInfo = jwt_decode(token);
    console.log(userInfo);
  },
  hello: (loginData) => {
    axios
      .get("http://localhost:8080/api/hello", {
        withCredentials: true,
        origin: "http://localhost:8080",
      })
      .then(function (response) {
        console.log("로그인 성공");
        console.log(response);
      })
      .catch(function (error) {
        console.log("로그인 실패");
        console.log(error);
      });
  },
});
let userInfo = observable({
  sub: null,
  role: null,
});
let authorizationInfo = observable({
  isAuthorized: false,
});

export default authMethods;
export { userInfo, authorizationInfo };
