import axios from "axios";
import { observable, runInAction, toJS } from "mobx";
import jwt_decode from "jwt-decode";

let authMethods = observable({
  login: (loginData) => {
    console.log(loginData);
    axios
      .post("http://localhost:8080/api/authenticate", {
        withCredentials: true,
        origin: "http://localhost:8080",
        username: loginData.username,
        password: loginData.password,
      })
      .then(function (response) {
        console.log("로그인 성공");
        // console.log(response);
        authMethods.setDefaultAxiosRequestHeader(response);
        authMethods.setUserInfo(response);
        console.log("!!");
      })
      .catch(function (error) {
        console.log("로그인 실패");
        console.log(error);
      });
  },
  setDefaultAxiosRequestHeader: (response) => {
    const { accessToken } = response.data;
    // console.log(response.data);
    // accessToken 설정
    axios.defaults.headers.common["Authorization"] = `Bearer ${accessToken}`;
  },
  setUserInfo: (response) => {
    const { accessToken } = response.data;
    userInfo = jwt_decode(`Bearer ${accessToken}`, { header: true });
    console.log(userInfo);
  },
  hello: (loginData) => {
    axios
      .get("http://localhost:8080/api/hello", {
        withCredentials: true,
        origin: "http://localhost:8080",
      })
      .then(function (response) {
        console.log("로그인 성공");
        console.log(response);
      })
      .catch(function (error) {
        console.log("로그인 실패");
        console.log(error);
      });
  },
});
let userInfo = observable({
  username: null,
  nickname: null,
});

export default authMethods;
export { userInfo };
